// Get the Gemini output text
const rawText =$json.content.parts[0].text ;
// Use a regular expression to find the content inside the ```json...``` block
const match = rawText.match(/```json\n([\s\S]*?)\n```/);

let jsonObject = {};

if (match && match[1]) {
  // If a match is found, parse the captured group (the content inside the block)
  try {
    jsonObject = JSON.parse(match[1]);
  } catch (e) {
    // Handle parsing errors if the output is not valid JSON
    console.error('Failed to parse JSON:', e);
    // You might want to throw an error here or set a fallback value
  }
} else {
  // If no code block is found, assume the entire text is the JSON
  try {
    jsonObject = JSON.parse(rawText);
  } catch (e) {
    console.error('Failed to parse JSON directly:', e);
    // Handle error
  }
}

// Return the cleaned JSON object
return { jsonObject };

